// Directly using the file from previous node
const file = $json;

if (!file.id) {
  throw new Error("File ID is missing.");
}

// Return clean JSON with file details
return [{
  json: {
    fileId: file.id,
    fileName: file.name || "Unknown",
    folderName: $json.folderName || "Unknown"
  }
}];

const text = $json["userCommand"] || "";
const parts = text.trim().split(/\s+/);

// Expected format: MOVE <fileName> TO <folderName>
if (
  parts.length !== 4 ||
  parts[0].toUpperCase() !== "MOVE" ||
  parts[2].toLowerCase() !== "to"
) {
  throw new Error("Invalid format. Use: MOVE <fileName> TO <folderName>");
}

return [{
  json: {
    fileName: parts[1],
    toFolder: parts[3]
  }
}];

// Fetch outputs from both nodes via Merge (wait for all)
const fileResults = $items("Search files and folders2");
const folderResults = $items("Search files and folders3");

// Ensure both searches returned results
if (!fileResults || fileResults.length === 0) {
  throw new Error("File not found. 'Search files and folders2' returned no results.");
}
if (!folderResults || folderResults.length === 0) {
  throw new Error("Folder not found. 'Search files and folders3' returned no results.");
}

// Use the first match from each
const file = fileResults[0].json;
const folder = folderResults[0].json;

// Return the IDs for the Update (Move) node
return [{
  json: {
    fileId: file.id,
    toFolderId: folder.id,
    fileName: file.name,
    folderName: folder.name
  }
}];

const text = $json["userCommand"] || "";
const folderName = text.split(" ")[1]?.replace("/", "").trim();

if (!folderName) {
  throw new Error("Folder name not found. Use format: SUMMARY /<FolderName>");
}

return [{
  json: {
    folderName: folderName
  }
}];

const text = $json["text"] || "";

return [
  {
    json: {
      summary: text.split('.').slice(0, 3).join('. ') + '.',
    }
  }
];

const file = items.find(item => item.json.name && item.json.id);

if (file) {
  return [
    {
      json: {
        timestamp: new Date().toISOString(),
        command: 'LIST',
        parameters: '',
        from: 'system',
        fileName: file.json.name,
        fileId: file.json.id,
        result: 'File Found'
      }
    }
  ];
}

// fallback
return [
  {
    json: {
      timestamp: new Date().toISOString(),
      command: 'UNKNOWN',
      parameters: '',
      from: 'unknown',
      result: 'No Valid File Found'
    }
  }
];

return [
  {
    json: {
      timestamp: new Date().toISOString(),
      command: 'DELETE',
      fileName: $json["fileName"] || 'Unknown',
      from: $json["from"] || 'system',
      result: 'File Deleted'
    }
  }
];

return [
  {
    json: {
      timestamp: new Date().toISOString(),
      command: 'MOVE',
      fileName: $json["fileName"] || 'Unknown',
      from: $json["from"] || 'system',
      sourceFolder: $json["sourceFolderName"] || 'Unknown',
      destinationFolder: $json["foldername"] || 'Unknown',
      result: 'File Moved'
    }
  }
];

return [
  {
    json: {
      timestamp: new Date().toISOString(),
      command: 'SUMMARY',
      fileName: $json["fileName"] || 'Unknown',
      from: $json["from"] || 'system',
      result: 'Summary Provided'
    }
  }
];

const input = $json["userCommand"].trim().split(" ");
return [
  {
    json: {
      fileName: input.slice(2).join(" ").toLowerCase()
    }
  }
];
